package com.imlianai.zjdoll.app.modules.support.report.cmd;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import java.util.ArrayList;import java.util.List;import javax.annotation.Resource;import javax.ws.rs.Path;import org.springframework.stereotype.Component;import com.imlianai.rpc.support.common.cmd.BaseRespVO;import com.imlianai.rpc.support.manager.aspect.annotations.ApiHandle;import com.imlianai.rpc.support.manager.aspect.annotations.LoginCheck;import com.imlianai.zjdoll.app.controller.RootCmd;import com.imlianai.zjdoll.app.modules.support.report.service.ReportService;import com.imlianai.zjdoll.app.modules.support.report.vo.ReportWordListReqVo;import com.imlianai.zjdoll.app.modules.support.report.vo.ReportWordListRespVo;/** * 获取屏蔽关键字 *  * @author Max *  */@Component("reportWord")@Api("关键字")public class CmdReportWord extends RootCmd {	@Resource	private ReportService reportService;	@ApiHandle	@Path("/api/reportWord/list")	@ApiOperation(value = "【1.0.0】获取关键字列表接口", notes = "获取关键字列表", httpMethod = "POST", response = ReportWordListRespVo.class)	public BaseRespVO list(ReportWordListReqVo vo) {		ReportWordListRespVo resp = new ReportWordListRespVo();		List<String> addWords=reportService.getWords(vo.getUid(), vo.getImei(), 1);		resp.setAddWords(addWords);		List<String> delWords=new ArrayList<String>();		delWords.add("拉流");		resp.setDelWords(delWords);		return resp;	}}